import React, { useState } from "react";
import "./work.css";
import Card from "./Card.jsx";

const Data = [
  {
    id: 1,
    category: "experience",
    icon: "icon-briefcase",
    year: "MAY 22 - Current",
    title: "Website Developer",
    desc: "the Social Media Network",
  },
  {
    id: 2,
    category: "experience",
    icon: "icon-briefcase",
    year: "SEP 21 - FEB 22",
    title: "Website Developer",
    desc: "Tech Creative Partner",
  },

  {
    id: 3,
    category: "education",
    icon: "icon-graduation",
    year: "NOV 19 - APR 21",
    title: "Master of Information Technology",
    desc: "University of Sydney",
  },
  {
    id: 4,
    category: "education",
    icon: "icon-graduation",
    year: "FEB 19 - NOV 19",
    title: "Graduate Diploma in Information Technology",
    desc: "University of Sydney",
  },
];

const [open, setOpen] = useState(false);

const Work = () => {
  return (
    <section className="about container section" id="experience">
      <h2 className="section-title">- Experience</h2>
      <div className="work-container grid">
        <div className="timeline grid">
          {Data.map((val, index) => {
            if (val.category === "experience") {
              return (
                <Card
                  key={index}
                  icon={val.icon}
                  title={val.title}
                  year={val.year}
                  desc={val.desc}
                />
              );
            }
          })}
          <p className="click-view" onClick={() => setOpen(!open)}>
            click to view more
          </p>
        </div>

        <div className="timeline grid">
          {Data.map((val, id) => {
            if (val.category === "education") {
              return (
                <Card
                  key={id}
                  icon={val.icon}
                  title={val.title}
                  year={val.year}
                  desc={val.desc}
                />
              );
            }
          })}
        </div>
      </div>

      <div className={open ? "work-details open" : "work-details"}>
        <i className="icon-close"></i>
        <h3>Website Developer (May 22 - Current)</h3>
        <h6>the Social Media Network</h6>

        <p>Key responsibilities:</p>
        <ul>
          <li>
            Develop and maintain websites by WordPress with different page
            builders, like Elementor, WPBakery, Divi and Visual Composer.
          </li>
          <li>
            Use and investigate suitable plugins to integrate the websites, like
            Woocommerce, Mailchimp, All-in-one for different purposes.
          </li>
          <li>Connect to APIs by Zappier to transfer data to the database.</li>
          <li>Customize websites by HTML, CSS and jQuery.</li>
          <li>Host websites on the server, like Ventraip, Linode, anet.</li>
          <li>
            Optimise websites by Search engine optimization (SEO), ahrefs,
            google analytics and google search console.
          </li>
        </ul>

        <p>Achievements:</p>
        <ul>
          <li>
            Developed more than 30 websites from the beginning until to launch.
          </li>
          <li>
            Assisted UI designer to create the prototypes and find usable
            templates and plugins on Themeforest and Envato.
          </li>
          <li>Prepared website proposal to present to clients.</li>
        </ul>
      </div>
    </section>
  );
};

export default Work;
