  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<Error | undefined>(undefined);

  async function handleShare(options: ShareOptions) {
    try {
      const {
        href,
        display,
        hashtag,
        redirectUri,
        ...rest
      } = options;

      setError(undefined);
      setIsLoading(true);

      const api = await init();
      if (!api) {
        throw new Error('Facebook API is not initialized');
      }

      return api.ui(clearUndefinedProperties({
        method: 'share',
        href,
        display,
        app_id: api.getAppId(),
        hashtag,
        redirect_uri: redirectUri,
        ...rest,
      }));
    } catch (error) {
      setError(error as Error);
    } finally {
      setIsLoading(false);
    }
  }

  return {
    isLoading,
    error,
    share: handleShare,
  };
}
type NonNullProperties<T> = { 
  [K in keyof T as T[K] extends undefined ? never : K]: T[K];
};

export default function clearUndefinedProperties<T extends {}, V = NonNullProperties<T>>(obj: T): V {
  return Object.fromEntries(Object.entries(obj).filter(([, value]) => value !== undefined)) as V;
}
