import { useCallback, useEffect, useState } from 'react';
import useFacebook from './useFacebook';

export default function useSubscribe<T>(event: string, callback?: (data: any) => void): T | undefined {
  const [lastValue, setLastValue] = useState<T | undefined>(undefined);
  const { init } = useFacebook();

  const handleResponse = useCallback((value: T) => {
    setLastValue(value);
    callback?.(lastValue);
  }, [callback]);

  const handleSubscribe = useCallback(async () => {
    const api = await init();
    if (api) {
      await api.subscribe(event, handleResponse);
    }
  }, [event, handleResponse]);

  const handleUnsubscribe = useCallback(async () => {
    const api = await init();
    if (api) {
      await api.unsubscribe(event, handleResponse);
    }
  }, [event, handleResponse]);


  useEffect(() => {
    handleSubscribe();

    return () => {
      handleUnsubscribe();
    };
  }, [handleSubscribe]);

  return lastValue;
}
import { useEffect, useState, useCallback } from 'react';
import useFacebook from './useFacebook';
import useSubscribe from './useSubscribe';
import LoginStatus from '../constants/LoginStatus';

export default function useLoginStatus(): {
  isLoading: boolean;
  error?: Error;
  status?: LoginStatus;
} {
  const { init } = useFacebook();
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<Error | undefined>(undefined);
  const [status, setStatus] = useState<LoginStatus>(LoginStatus.UNKNOWN);

  const handleStatusChanges = useCallback((response: { status: LoginStatus }) => {
    setStatus(response.status);
  }, []);

  useSubscribe('auth.statusChange', handleStatusChanges);

  async function handleGetInitState() {
    try {
      setIsLoading(true);
      const api = await init();
      if (!api) {
        throw new Error('Facebook API is not initialized');
      }

      const { status } = await api.getLoginStatus();
      setStatus(status);
    } catch (error) {
      setError(error as Error);
    } finally {
      setIsLoading(false);
    }
  }

  useEffect(() => {
    handleGetInitState();
  }, []);

  return {
    isLoading,
    error,
    status,
  };
}
import { useState, useEffect } from 'react';
import LoginStatus from '../constants/LoginStatus';
import useFacebook from './useFacebook';
import useLoginStatus from "./useLoginStatus";

export default function useProfile(fields: string[]) {
  const { init } = useFacebook();
  const { status } = useLoginStatus();

  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [profile, setProfile] = useState<any>(undefined);
  const [error, setError] = useState<Error | undefined>(undefined);

  async function updateProfile() {
    try {
      setError(undefined);
      setIsLoading(true);

      const api = await init();
      if (!api) {
        throw new Error('Facebook API is not initialized');
      }

      if (status === LoginStatus.CONNECTED) {
        const profile = await api.getProfile({
          fields,
        });

        setProfile(profile);
      }
    } catch (error) {
      setError(error as Error);
    } finally {
      setIsLoading(false);
    }
  }

  useEffect(() => {
    updateProfile();
  }, [status, fields]);

  return {
    isLoading,
    error,
    profile,
  };
}
